import React, {useState , useEffect} from "react";
import { useParams } from "react-router";
import Comments from "./Comments";
import CommentForm from "./CommentForm";
import ItemUpdateForm from "./ItemUpdateForm";

function ItemDetailPage() {
const params = useParams();

const [itemInfo, setItemInfo] = useState([])

function handleUpdateItem(item) {
    setItemInfo(item)
  }

function handleDelete(id, itemId) {
    console.log(`comment id ${id} and item id ${itemId}` )
    fetch(`http://localhost:9292/comments/${id}`, {
      method: "DELETE",
    })
    .then((r)=> {
      if(r.status === 200) {
        handleUpdateItem(itemId)
      }
    });
}



useEffect(() => {
fetch(`http://localhost:9292/closet/${params.id}`)
.then((r)=>r.json())
.then((data) => setItemInfo(data))
}, [])

const commentData = itemInfo.comments
return(
<div className="card">
    <h3>Selected Item</h3>
    <img src={itemInfo.url} alt={itemInfo.name} />
    <h4>{itemInfo.name}</h4>
    <h5>${`${itemInfo.price}`}</h5>
    <h5>{`Purachased on: ${itemInfo.purchase_date}`}</h5>
    <h5>{`Last worn on: ${itemInfo.last_worn}`}</h5>
    <h3>Comments</h3>
    {commentData && commentData.map((comment) => (
        <div key={comment.id}>
        <h5>{comment.comment}</h5>
        <h6>{comment.created_at}</h6>
        <button onClick={()=>handleDelete(comment.id, itemInfo.id)} id={comment.id}>X</button>
        </div>
    ))}
<CommentForm article_id={itemInfo.id}/>
</div>
)
}
export default ItemDetailPage;

