import React, {useState} from "react";

function ItemUpdateForm({itemInfo, handleUpdateItem}) {
  const initialFormData = {last_worn: ""
  }
  const [formData, setFormData] = useState(initialFormData)

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]:e.target.value
    })
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const updatedItem = {
      ...formData
    }
    console.log(updatedItem)
    fetch(`http://localhost:9292/closet/${itemInfo.id}`, {
      method: "PATCH",
      headers: {
        "Content-Type": "application/json",
        },
        body: JSON.stringify(updatedItem),
    })
        .then((r) => r.json())
        .then((item) => console.log(item))
        setFormData(initialFormData)
  };

  return (
    <div className="item-update-form">
      <h2>Update Item Details</h2>
      <form onSubmit={handleSubmit}>
        <input type="date" name="last_worn" placeholder={itemInfo.last_worn} value={formData.last_worn} onChange={handleChange}/>
        <button type="submit">Update Item</button>
      </form>
    </div>
  );
}


export default ItemUpdateForm;
