import React, {useState} from "react";

function ItemUpdateForm({id, handleUpdateItem}) {
  const initialFormData = {
    name: "",
    image: "",
    price: "",
    date: ""
  }
  const [formData, setFormData] = useState(initialFormData)

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]:e.target.value
    })
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const updatedItem = {
      ...formData
    }
    console.log(updatedItem)
    fetch("http://localhost:9292/closet", {
      method: "PATCH",
      headers: {
        "Content-Type": "application/json",
        },
        body: JSON.stringify(updatedItem),
    })
        .then((r) => r.json())
        .then((item) => handleUpdateItem(item))

        setFormData(initialFormData)
  };

  return (
    <div className="new-item-form">
      <h2>Add to Closet</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="name" placeholder="Update Item Name" value={formData.name} onChange={handleChange}/>
        <input type="text" name="image" placeholder="Update Image" value={formData.image} onChange={handleChange}/>
        <input type="number" name="price" placeholder="Update Price" value={formData.price} onChange={handleChange}/>
        <input type="date" name="date" placeholder="Update Last Worn" value={formData.date} onChange={handleChange}/>
        <button type="submit">Update Item</button>
      </form>
    </div>
  );
}


export default ItemUpdateForm;
