import React, {useState} from "react";

function ItemUpdateForm({itemInfo, id, handleUpdateItem}) {
  const initialFormData = {
    name: itemInfo.name,
    image: itemInfo.url,
    price: itemInfo.price,
    date: itemInfo.last_worn
  }
  const [formData, setFormData] = useState(initialFormData)

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]:e.target.value
    })
    console.log(formData)
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const updatedItem = {
      ...formData
    }
    console.log(updatedItem)
    fetch(`http://localhost:9292/closet/${itemInfo.id}`, {
      method: "PATCH",
      headers: {
        "Content-Type": "application/json",
        },
        body: JSON.stringify(updatedItem),
    })
        .then((r) => r.json())
        .then((item) => console.log(item))

        setFormData(initialFormData)
  };

  return (
    <div className="item-update-form">
      <h2>Update Item Details</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="name" placeholder={itemInfo.name} value={formData.name} onChange={handleChange}/>
        <input type="text" name="image" placeholder={itemInfo.url} value={formData.image} onChange={handleChange}/>
        <input type="number" name="price" placeholder={itemInfo.price}value={formData.price} onChange={handleChange}/>
        <button type="submit">Update Item</button>
      </form>
    </div>
  );
}


export default ItemUpdateForm;
