import React, {useState , useEffect} from "react";
import { useParams } from "react-router";
import Comments from "./Comments";
import CommentForm from "./CommentForm";

function ItemDetailPage() {
const params = useParams();

const [itemInfo, setItemInfo] = useState([])

useEffect(() => {
fetch(`http://localhost:9292/closet/${params.id}`)
.then((r)=>r.json())
.then((data) => setItemInfo(data))
}, [])

const comments = itemInfo.comments
console.log(comments)

//const commentsToRender = comments.map((comment)=>
     //   <div key={comment.id}>
    //</div>) 
         //   <h5>{comment.comment}</h5>
          //  <h6>{comment.created_at}</h6>
    //<button onClick={handleDelete} id={comment.id}>X</button>
return(
<div className="card">
    <h3>Selected Item</h3>
    <img src={itemInfo.url} alt={itemInfo.name} />
    <h4>{itemInfo.name}</h4>
    <h5>{`${itemInfo.price}`}</h5>
    <h5>{`Purachased on: ${itemInfo.purchase_date}`}</h5>

    <CommentForm article_id={itemInfo.id}/>
</div>
)
}
export default ItemDetailPage;
